<!-- - - - Parc de voitures - - - --> <?php require 'voiture.php'; $marque = 'Audi'; $poids = 1500; $immatriculation = "DE-5792"; $kilometrage = 100000; $dateCirculation = 2016; $move = 150000; $vehicule = new Voiture($marque, $poids, $immatriculation, $kilometrage, $dateCirculation, $move); echo $vehicule->disponible(); echo $vehicule->poids(); echo $vehicule->plaque(); echo $vehicule->usure(); echo $vehicule->age(); echo $vehicule->drive(); ?>
<?php class Voiture { private $marque; public $poids; private $immatriculation; public $kilometrage; private $dateCirculation; private $model; public $couleur; public $mouvement; public function __construct($marque, $poids, $immatriculation, $kilometrage, $dateCirculation, $mouvement) { $this->marque = $marque; $this->poids = $poids; $this->immatriculation = $immatriculation; $this->kilometrage = $kilometrage; $this->dateCirculation = $dateCirculation; $this->move = $mouvement; } // On afficher si la voiture est dispo ou réservée public function disponible() { if($this->marque == 'Audi'){ echo 'Cette '.$this->marque.' est réservée !'; echo '<hr>'; } else { echo 'Cette '.$this->marque.' est disponible !'; echo '<hr>'; } } // On classe par type de véhicule en fonction du poids public function poids() { if ($this->poids > 3500) { echo 'Cette voiture pèse '.$this->poids.' Kg et est de type utilitaire.'; echo '<hr>'; } else { echo 'Cette voiture pèse '.$this->poids.' Kg et est de type commerciale.'; echo '<hr>'; } } // On indique de quel pays vient la voiture public function plaque() { if (stristr($this->immatriculation, 'BE') == True) { echo 'Cette voiture est immatriculée en Belgique.'; echo '<hr>'; } elseif (stristr($this->immatriculation, 'FR') == True) { echo 'Cette voiture est immatriculée en France.'; echo '<hr>'; } elseif (stristr($this->immatriculation, 'DE') == True) { echo 'Cette voiture est immatriculée en Allemagne.'; echo '<hr>'; } else { echo 'Cette voiture est immatriculée d'un pays inconnu.'; echo '<hr>'; } } // On indique à quel point le véhicule a servi public function usure() { if ($this->kilometrage >= 200000) { echo 'Cette voiture a fortement servie. Elle a '.$this->kilometrage.' Km au compteur.'; echo '<hr>'; } elseif ($this->kilometrage > 100000 && $this->kilometrage < 200000) { echo 'Cette voiture a moyennement servie. Elle a '.$this->kilometrage.' Km au compteur.'; echo '<hr>'; } elseif ($this->kilometrage <= 100000) { echo 'Cette voiture a peu servie. Elle a '.$this->kilometrage.' Km au compteur.'; echo '<hr>'; } } // On affiche l'age du véhicule public function age() { $annee = date('Y'); $age = $annee - $this->dateCirculation; echo 'Cette voiture a '.$age.' ans.'; echo '<hr>'; } // On modifie le compteur et l'usure du véhicule public function drive() { $this->kilometrage += $this->move; echo 'Après avoir roulé sur '.$this->move.' KM, le compteur de la voiture affiche maintenant '.$this->kilometrage.' KM.'; echo '<hr>'; } // // On affiche une photo et les caractéristiques du véhicule // public function display() // { // #code... // } } ?>
